//     price: 18.99,
//   },
// ];



const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorHandle, setErrorHandle] = useState(false);
  // useEffect(() => {
  //   const fetchMeals = async () => {
  //     const respose = await fetch("https://react-https-5fa64-default-rtdb.firebaseio.com/meals.json");

  //     if(!respose.ok){
  //       throw new Error("Something went wrong");
  //     }

  //     const responseData = await respose.json();

  //     const loadedMeals = [];

  //     for(const key in responseData){
  //       loadedMeals.push({
  //         id: key,
  //         name: responseData[key].name,
  //         description : responseData[key].description,
  //         price: responseData[key].price
  //       })
  //     }
    
  //     setMeals(loadedMeals);

  //     setLoading(false);

  //   };

    
  //   fetchMeals().catch((error) => {
  //     setLoading(false);
  //     setErrorHandle(true);
  //   })
    
  // }, []);
  
  useEffect(() => {
    const loadedMeals = [];
    const fetchData = () => {
      const colref = collection(db, 'Meal');
      onSnapshot(colref, (snapshot) => {
        snapshot.docs.forEach((docs) => {
          loadedMeals.push({
            id: docs.id,
            name: docs.data().name,
          description : docs.data().description,
          price: docs.data().price
          })
          // console.log(loadedMeals)
        })
      })

      setMeals(loadedMeals);
      setLoading(false);
    }

    fetchData().catch((err) => {
      setLoading(false);
      setErrorHandle(true);
      
    });

  }, []);

  console.log(meals);


  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id = {meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  console.log(mealsList);



  return (
    <section className={classes.meals}>
      <Card>
        {errorHandle && <div className={classes.meal_load}>Something went wrong</div>}
        {loading && <div className={classes.meal_load}>Loading Meals ...</div>}
        {!loading && <ul>{mealsList}</ul>}
      </Card>
    </section>
  );
};

export default AvailableMeals;
