  //     onSnapshot(colref, (snapshot) => {
  //       snapshot.docs.forEach((docs) => {
  //         loadedMeals.push({
  //           id: docs.id,
  //           name: docs.data().name,
  //         description : docs.data().description,
  //         price: docs.data().price
  //         })
  //         // console.log(loadedMeals)
  //       })
  //     })

  //     setMeals(loadedMeals);
  //     setLoading(false);
  //   }

  //   fetchData().catch((err) => {
  //     setLoading(false);
  //     setErrorHandle(true);
      
  //   });

  // }, []);

  // console.log(meals);


  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id = {meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));
  console.log(mealsList);



  return (
    <section className={classes.meals}>
      <Card>
        {errorHandle && <div className={classes.meal_load}>Something went wrong</div>}
        {loading && <div className={classes.meal_load}>Loading Meals ...</div>}
        {!loading && <ul>{mealsList}</ul>}
      </Card>
    </section>
  );
};

export default AvailableMeals;
