import { useEffect, useState } from "react";
import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItem/MealItem";

// const DUMMY_MEALS = [
//   {
//     id: "m1",
//     name: "Sushi",
//     description: "Finest fish and veggies",
//     price: 22.99,
//   },
//   {
//     id: "m2",
//     name: "Schnitzel",
//     description: "A german specialty!",
//     price: 16.5,
//   },
//   {
//     id: "m3",
//     name: "Barbecue Burger",
//     description: "American, raw, meaty",
//     price: 12.99,
//   },
//   {
//     id: "m4",
//     name: "Green Bowl",
//     description: "Healthy...and green...",
//     price: 18.99,
//   },
// ];



const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errorHandle, setErrorHandle] = useState(false);
  useEffect(() => {
    const fetchMeals = async () => {
      const respose = await fetch("https://react-https-5f64-default-rtdb.firebaseio.com/meals.json");

      if(!respose.ok){
        throw new Error("Something went wrong");
      }

      const responseData = await respose.json();

      const loadedMeals = [];

      for(const key in responseData){
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description : responseData[key].description,
          price: responseData[key].price
        })
      }
    
      setMeals(loadedMeals);

      setLoading(false);

    };

    try{
      fetchMeals();
    }
    catch(error){
      console.log("Haan bhyii")
      setLoading(false);
      setErrorHandle(true);
    }
  }, []);
  
  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id = {meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        {errorHandle && <div className={classes.meal_load}>Something went wrong</div>}
        {loading && <div className={classes.meal_load}>Loading Meals ...</div>}
        {!loading && <ul>{mealsList}</ul>}
      </Card>
    </section>
  );
};

export default AvailableMeals;
